css:
@import url('https://fonts.googleapis.com/css2?family=Quicksand:wght@700&display=swap');


body {
    margin: 0;
    font-family: 'Quicksand', -apple-system,BlinkMacSystemFont,Segoe UI,Roboto,Helvetica Neue,Arial,Noto Sans,sans-serif,Apple Color Emoji,Segoe UI Emoji,Segoe UI Symbol,Noto Color Emoji,sans-serif;
    font-size: 1rem;
    font-weight: 400;
    line-height: 1.5;
    color: #212529;
    text-align: left;
    background-color: #fff;
}

a, b, body, em, h1, h2, h3, h4, h5, h6, li, span, strong, ul {
    font-family: 'Quicksand';
    font-style: normal;
    -webkit-font-smoothing: antialiased;
    -webkit-text-stroke-width: .2px;
    -moz-osx-font-smoothing: grayscale;
}
@decimal-format .prices__value {
  grouping-separator: ",";
  decimal-separator : "."
}

.header__nav a {
    padding-right: 1rem;
}

.top-bar__nav {
    max-width: 80rem;
    color: #ffffff;
    margin-left: auto;
    background-color: #000000;
    margin-right: auto;
    padding: var(--spacer-2) var(--spacer-2) var(--spacer-1);
}

.top-bar a.top-bar__countdown {
    background: var(--primary);
    color: var(--primary-yiq);
    font-weight:800;
    font-size:20px;
}

.top-bar__page-links a {
    margin-right: var(--spacer-3);
    font-weight: 800;
    /* font-size: 16px; */
}

.top-bar__nav a {
    display: inline-block;
    margin-right: var(--spacer-2);
    color: inherit;
    font-weight: 700;
}

.glide__arrow {
    z-index: 2;
    opacity: .7;
    cursor: pointer;
    position: absolute;
    display: block;
    top: 50%;
    transform: translateY(-50%);
    font-size: 1.2rem;
    line-height: 1;
    transition: opacity .2s;
    background: transparent;
    color: #e8e8e8;
    border: none;
}
.footer {
    background: #000000;
    border-top: var(--spacer-1) solid var(--secondary);
    color: #e6e6e6;
}

.footer__title {
    font-size: var(--font-size-lg);
    font-weight: var(--font-light);
    margin-bottom: var(--spacer-2);
    display: block;
    color: #f9f9f9;
    outline: none;
}

.footer__store {
    color: #e2e2e2;
    font-weight: var(--font-headings);
}

.footer, .footer__social, .footer__social .fa-facebook, .footer__social .fa-instagram, .footer__title, .footer a, .footer h2 {
    color: #fff;
}
.footer a:hover {
    color: #fff;
    text-decoration: underline;
}
.top-bar {
    background: #000000;
    width: 100%;
}
